import React from "react";
import { COLORS } from "./Colors";
import { Button, Collapse } from 'react-bootstrap'; // Import Bootstrap components
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faCoins,
  faCompactDisc,
  faHeart,
  faMicrophone,
  faPlay,
  faPause,
  faTag,
  faShoppingCart,
  faFileContract
} from "@fortawesome/free-solid-svg-icons";

const Web3 = require('web3');

// Replace with your Ethereum node URL
const nodeUrl = 'HTTP://127.0.0.1:7545';

const web3 = new Web3(nodeUrl);

web3.eth.getBlockNumber()
  .then(console.log)
  .catch(console.error);

class SongCard extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      playing: false,
      audio: "",
      hash: "",
      currentTime: 0,
      duration: 0,
      showDetails: false,
    };
  }

  componentDidMount() {
    this.loadSong();
  }



  buySong = async () => {
    console.log(`Buying song with ID: ${this.props.songID}, from account: ${this.props.account}, with cost: ${this.props.cost}`);
    console.log("Contract Instance: ", this.props.contract)
    try {
      const contractInstance = await this.props.contract.deployed();
      await contractInstance.buySong(this.props.songID, { from: this.props.account, value: this.props.cost });
      console.log("Song purchased successfully.");
    } catch (error) {
      console.log("Error purchasing song:", error);
    }
  }

  loadSong = async () => {
    let audio_src = "https://ipfs.infura.io/ipfs/" + this.props.hash;
    let audio = new Audio(audio_src);
    audio.addEventListener("timeupdate", this.updateTime);
    audio.addEventListener("loadedmetadata", () => {
      this.setState({ duration: audio.duration });
    });
    this.setState({ audio });
  };

  updateTime = () => {
    this.setState({ currentTime: this.state.audio.currentTime });
  };

  playSong = async () => {
    if (this.state.audio) {
      this.setState({ playing: true });
      this.state.audio.play();
    }
  };

  pauseSong = async () => {
    if (this.state.audio) {
      this.setState({ playing: false });
      this.state.audio.pause();
    }
  };

  handleSliderChange = (e) => {
    this.state.audio.currentTime = e.target.value;
    this.setState({ currentTime: e.target.value });
  };

  toggleDetails = () => {
    this.setState({ showDetails: !this.state.showDetails });
  };

  render() {
    if (this.props.type === "artist")
      return (
        <div style={styles.card}>
          <h5>
            {" "}
            <FontAwesomeIcon icon={faCompactDisc} /> {this.props.name}{" "}
          </h5>
          <h5>
            {" "}
            <FontAwesomeIcon icon={faTag} /> {this.props.genre}{" "}
          </h5>
          <h5>
            {" "}
            <FontAwesomeIcon icon={faCoins} /> {this.props.cost / 1000000000000000}{" "}
          </h5>
          <h5>
            {" "}
            <FontAwesomeIcon icon={faFileContract} /> {this.props.likes}{" "}
          </h5>
        </div>
      );
    else if (this.props.type === "publisher") {
      const cardStyle = {
        padding: "2%",
        borderRadius: "10px",
        border: "2px solid",
        boxShadow: "1px 3px 1px #191919",
        borderColor: COLORS.black,
        backgroundColor: COLORS.white,
        marginBottom: "10px", // Add some space between cards
        display: "flex",
        flexDirection: this.state.showDetails ? "column" : "row",
        justifyContent: "space-between",
        alignItems: "center",
        height: this.state.showDetails ? "auto" : "20%",
      };

      return (
        <div style={cardStyle}>
          {!this.state.showDetails && (
            <>
              <h5>
                <FontAwesomeIcon icon={faCompactDisc} /> {this.props.name}
              </h5>
              <h5>
                <FontAwesomeIcon icon={faTag} /> {this.props.genre}
              </h5>
              <h5>
                <FontAwesomeIcon icon={faCoins} /> {this.props.cost_personal / 1000000000000000}
              </h5>
              <h5>
                <FontAwesomeIcon icon={faCoins} /> {this.props.cost_commercial / 1000000000000000}
              </h5>
              <h5>
                <FontAwesomeIcon icon={faFileContract} /> {this.props.times_personal_purchased}
              </h5>
              <h5>
                <FontAwesomeIcon icon={faFileContract} /> {this.props.times_commercial_purchased}
              </h5>
            </>
          )}
          <Button onClick={this.toggleDetails}>
            {this.state.showDetails ? "Hide Details" : "Show Details"}
          </Button>
          <Collapse in={this.state.showDetails}>
            <div>
              <h5>
                <FontAwesomeIcon icon={faCompactDisc} /> Song Name: {this.props.name}
              </h5>
              <h5>
                <FontAwesomeIcon icon={faTag} /> {this.props.genre}
              </h5>
              <h5>
                <FontAwesomeIcon icon={faCoins} /> {this.props.cost_personal / 1000000000000000}
              </h5>
              <h5>
                <FontAwesomeIcon icon={faCoins} /> {this.props.cost_commercial / 1000000000000000}
              </h5>
              <h5>
                <FontAwesomeIcon icon={faFileContract} /> {this.props.times_personal_purchased}
              </h5>
              <h5>
                <FontAwesomeIcon icon={faFileContract} /> {this.props.times_commercial_purchased}
              </h5>
            </div>
          </Collapse>
        </div>
      );
    }
    else if (this.props.type === "audience") {
      // const artistNames = this.props.artist.join(', ');
      // const displayArtist = this.props.artist.length === 1 ? artistNames : `${artistNames.slice(0, artistNames.lastIndexOf(','))} & ${this.props.artist[this.props.artist.length - 1]}`;
      return (
        <div style={!this.state.playing ? styles.card : styles.cardHiglight}>
          <h5>
            {" "}
            <FontAwesomeIcon icon={faCompactDisc} spin={this.state.playing} />{" "}
            {this.props.name}{" "}
          </h5>
          <h5>
            {" "}
            <FontAwesomeIcon icon={faTag} /> {this.props.genre}{" "}
          </h5>
          <h5>
            {" "}
            {/* <FontAwesomeIcon icon={faMicrophone} /> {displayArtist}{" "} */}
          </h5>
          <input
            type="range"
            min={0}
            max={this.state.duration}
            value={this.state.currentTime}
            onChange={this.handleSliderChange}
          />
          {this.state.playing ? (
            <h5>
              {" "}
              <FontAwesomeIcon onClick={this.pauseSong} icon={faPause} />{" "}
            </h5>
          ) : (
            <h5>
              {" "}
              <FontAwesomeIcon onClick={this.playSong} icon={faPlay} />{" "}
            </h5>
          )}
        </div>
      );
    }
    else if (this.props.type === "store") {
      return (
        <div style={styles.card}>
          <h5>
            {" "}
            <FontAwesomeIcon icon={faCompactDisc} /> {this.props.name}{" "}
          </h5>
          <h5>
            {" "}
            <FontAwesomeIcon icon={faTag} /> {this.props.genre}{" "}
          </h5>
          <h5>
            {" "}
            <FontAwesomeIcon icon={faMicrophone} /> {displayArtist}{" "}
          </h5>
          <h5>
            {" "}
            <FontAwesomeIcon icon={faCoins} /> {this.props.cost / 1000000000000000}{" "}
          </h5>
          <h5>
            {" "}
            <FontAwesomeIcon onClick={this.buySong} icon={faShoppingCart} />{" "}
          </h5>
        </div>
      );
    }
  }
}

const styles = {
  card: {
    height: "20%",
    width: "100%",
    padding: "2%",
    display: "flex",
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
    borderRadius: "10px",
    border: "2px solid",
    boxShadow: "1px 3px 1px #191919",
    borderColor: COLORS.black,
    backgroundColor: COLORS.white,
  },
  cardHiglight: {
    height: "20%",
    width: "100%",
    padding: "2%",
    display: "flex",
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
    borderRadius: "10px",
    border: "2px solid",
    boxShadow: "1px 3px 1px #191919",
    borderColor: COLORS.black,
    backgroundColor: COLORS.highlight,
  },
};

export default SongCard;
