import Web3 from 'web3'
import React from "react"

import {COLORS} from "./Colors"
import SongCard from "./SongCard"
import Loader from "react-loader-spinner"
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faHeadphones, faStore, faUser, faGift } from '@fortawesome/free-solid-svg-icons'


class Personal extends React.Component {

    constructor(props){
      super(props)
      this.web3 = new Web3(Web3.givenProvider || "http://localhost:7545")
      this.state = {name : "", personalID: "", store : [], library: [], songsMapping: {},searchTerm: "" }
    }
  
    componentDidMount() {
        this.loadStore().then(() => {
            console.log("Loaded Store");
            this.loadPersonalDetails().then(() => {
                console.log("Loaded Audience's Details");
                this.loadSongDetails().then(() => {
                    console.log("Loaded Songs");
                });
            });
        });
    }
    
    async loadStore() {
        try {
            const contractInstance = await this.props.contract.deployed();
            console.log("Personal Contract Instance: ",this.props.contract)
            const count = await contractInstance.getNumSongs({ from: this.props.account });
            const totalSongs = parseInt(count.toString()) + 1;
            console.log("Total songs:", totalSongs);
            
            let mapping = {};
            for (let i = 0; i <= totalSongs -1; i++) {
                mapping[i] = 0;
            }
            console.log(mapping);
            this.setState({ 
                songsMapping: mapping 
            });
        } catch (error) {
            console.error("Error loading store:", error);
        }
    }
    
    
    async loadPersonalDetails() {
      try {
          const contractInstance = await this.props.contract.deployed();
          const personalDetails = await contractInstance.getPersonalDetails({ from: this.props.account });
          console.log("personal Details", personalDetails);
  
          // Ensure songsMapping has keys for all possible song IDs
          let totalSongs = await contractInstance.getNumSongs({ from: this.props.account });
          totalSongs = parseInt(totalSongs.toString()) + 1; // Assuming getNumSongs returns the total number of songs
          let mapping = {};
          for (let i = 0; i < totalSongs; i++) {
              mapping[i] = 0;
          }
  
          // Update the values for the purchased songs
          for (let i = 0; i < personalDetails[3].length; i++) {
              let songID = parseInt(personalDetails[3][i].toString());
              mapping[songID] = 1;
          }
  
          this.setState({
              name: personalDetails[2].toString(),
              personalID: personalDetails[0].toString(),
              songsMapping: mapping
          });
      } catch (error) {
          console.error("Error loading audience details:", error);
      }
  }
    
    async loadSongDetails() {
        try {
            const contractInstance = await this.props.contract.deployed();
            let n = Object.keys(this.state.songsMapping).length;
            let newSongs = [];
            let purchasedSongs = [];
            console.log("Song Mapping",this.state.songsMapping)
    
            for (let i = 0; i <= n; i++) {
                let songDetails = await contractInstance.getSongDetails(i, { from: this.props.account });
                console.log("Song Details:", songDetails[0].toString()); // Debugging line
                if (this.state.songsMapping[i] === 1) {
                  
                    purchasedSongs.push({
                        'name': songDetails[2].toString(), // Ensure it's a string
                        'artist': songDetails[1].toString(), // Ensure it's a string
                        'genre': songDetails[7].toString(), // Ensure it's a string
                        'hash': songDetails[6].toString(), // Ensure it's a string
                        'cost': songDetails[3].toString(), // Ensure it's a string
                        'songID': songDetails[0].toString() // Ensure it's a string
                    });
                } else {
                    newSongs.push({
                        'name': songDetails[2].toString(), // Ensure it's a string
                        'artist': songDetails[1].toString(), // Ensure it's a string
                        'genre': songDetails[7].toString(), // Ensure it's a string
                        'hash': songDetails[6].toString(), // Ensure it's a string
                        'cost': songDetails[3].toString(), // Ensure it's a string
                        'songID': songDetails[0].toString() // Ensure it's a string
                    });
                }
            }
    
            this.setState({
                library: purchasedSongs,
                store: newSongs
            });
        } catch (error) {
            console.error("Error loading song details:", error);
        }
    }


    render() {
      if (this.state.personalID === "") {
          return (
              <div style={styles.main}>
                  <Loader type="Bars" color={COLORS.black} />
              </div>
          );
      } else {
          // Filter the store songs based on the search term
          const filteredStoreSongs = this.state.store.filter((song) =>
              song.name.toLowerCase().includes(this.state.searchTerm.toLowerCase())
          );
  
          return (
              <div style={styles.main}>
                  <div style={styles.info}>
                      <h2><FontAwesomeIcon icon={faHeadphones} />{this.state.name}</h2>
                      <h3>Personal ID: {this.state.personalID}</h3>
                  </div>
                  <div style={styles.library}>
                      <h3 style={{ textAlign: "center" }}><FontAwesomeIcon icon={faUser} /> Library</h3>
                      {this.state.library.map((item, i) => (
                          <SongCard
                              type={"audience"}
                              name={item.name}
                              artist={item.artist}
                              genre={item.genre}
                              hash={item.hash}
                              songID={item.songID}
                              key={i} />
                      ))}
                  </div>
                  <div style={styles.store}>
                      <h3 style={{ textAlign: "center" }}><FontAwesomeIcon icon={faStore} /> Store</h3>
                      <input
                          type="text"
                          placeholder="Search songs..."
                          value={this.state.searchTerm}
                          onChange={(e) => this.setState({ searchTerm: e.target.value })}
                          style={styles.searchInput} // Style for the search input
                      />
                      {filteredStoreSongs.length > 0 ? (
                          filteredStoreSongs.map((item, i) => (
                              <SongCard
                                  contract={this.props.contract}
                                  ipfs={this.props.ipfs}
                                  account={this.props.account}
                                  type={"shop"}
                                  name={item.name}
                                  artist={item.artist}
                                  genre={item.genre}
                                  cost={item.cost}
                                  hash={item.hash}
                                  songID={item.songID}
                                  key={i} />
                          ))
                      ) : (
                          <p style={{ textAlign: "center" }}>No songs available to purchase.</p>
                      )}
                  </div>
              </div>
          );
      }
  }
    }
    
    const styles = {
     main: {
        padding: "3%",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
        height: "110vh",
        gap: "5%",
        background: COLORS.white,
     },
     info: {
        marginBottom: "20px",
        textAlign: "center",
     },
     library: {
        width: "90%",
        padding: "25px",
        borderRadius: "30px",
        border: "3px solid",
        overflow: "auto",
        gap: "10px",
        boxShadow: "2px 5px 2px #191919",
        borderColor: COLORS.black,
        backgroundColor: COLORS.white,
     },
     store: {
        width: "90%",
        marginTop: "20px",
        padding: "25px",
        borderRadius: "30px",
        border: "3px solid",
        overflow: "auto",
        gap: "10px",
        boxShadow: "2px 5px 2px #191919",
        borderColor: COLORS.black,
        backgroundColor: COLORS.white,
     },
     searchInput: {
        width: "100%",
        padding: "10px",
        marginBottom: "10px",
        borderRadius: "5px",
        border: "1px solid #ccc",
     },
    };
    
    export default Personal;
