import React from "react"
import {COLORS} from "./Colors"
import SongCard from "./SongCard"
import AddSongCard from "./AddSongCard"
import Loader from "react-loader-spinner"
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faMicrophone, faPlusSquare ,faBuilding} from '@fortawesome/free-solid-svg-icons'

class Publisher extends React.Component {

    constructor(props){
      super(props);
      this.state = {name: "", artistID: "",artistsIDSong:[], publisherID: "",totalEarnings: "",purchased_times: 0, songIDs: [], songs: [], form : false,searchQuery: ""}
      this.openForm = this.openForm.bind(this);
      this.closeForm = this.closeForm.bind(this);
      this.handleSearchChange = this.handleSearchChange.bind(this); 
    }
  
    componentDidMount(){
        this.loadPublisherDetails().then(() =>{ console.log("Loaded Publisher's Details", this.state)
            this.loadSongDetails().then(() =>{ console.log("Loaded Publisher's Songs")})
        })
    }

    async loadPublisherDetails() {
        try {
            const contractInstance = await this.props.contract.deployed();
            const userType = await contractInstance.checkUserType.call({ from: this.props.account });
            console.log("User Type",userType)
            const publisherDetails = await contractInstance.getPublisherDetails.call({ from: this.props.account });
            let songList = [];
            for (let i = 0; i < publisherDetails[3].length; i++) {
                songList.push(publisherDetails[3][i].toString());
            }
            this.setState({
                publisherID: publisherDetails[0].toString(),
                name: publisherDetails[2].toString(),
                songIDs: songList
            });
        } catch (error) {
            console.error("Failed to load publisher details:", error);
        }
    }

    async getArtistName(artistID) {
        try {
            const contractInstance = await this.props.contract.deployed();
            // console.log('getArtistName call');
    
            // Correctly pass the artistID to the smart contract function
            const getArtistName = await contractInstance.getArtistNameByID(artistID, { from: this.props.account });
            console.log('artistID is\n', artistID);
    
            console.log('getArtistName', getArtistName);
            return getArtistName; // Assuming the function returns the artistName directly
        } catch (error) {
            console.error('getArtistName Error:', error);
            return ''; // Return an empty string or handle the error as needed
        }
    }
    
    // async loadSongDetails(){
    //     const contractInstance = await this.props.contract.deployed();
    //     let songInfoList = []
    //     for(let i = 0; i < this.state.songIDs.length; i++){
    //         let songDetails = await contractInstance.getSongDetails(this.state.songIDs[i], {from:this.props.account});
    //         console.log('State song id\n', this.state.songIDs[i])

    //         songInfoList.push({
    //             'name': songDetails[2],
    //             'genre': songDetails[7],
    //             'hash': songDetails[6],
    //             'cost_personal': songDetails[3].toString(),
    //             'cost_commercial': songDetails[4].toString(),
    //             'artistsIDSong':songDetails[8]
    //             // 'timesPurchased': songDetails[5].toString()
    //         });
    //         // this.state.purchased_times += parseInt(songDetails[5].toString());
    //     }
    //     console.log("Artist Songs:",songInfoList.artistsIDSong)
    //     console.log(songInfoList);

    //     this.setState({songs:songInfoList})
    // }

    async loadPurchasedTimes(_songID) {
        console.log("Into Purchased Times")
        console.log("songID:",_songID)
        let song_id = 0
        song_id = parseInt(_songID)
        // console.log(typeof song_id)
        // console.log(song_id)
        const contractInstance = await this.props.contract.deployed();
        const purchasedNumber = await contractInstance.getSongPurchasedTimes(song_id, { from: this.props.account });
        // console.log("Got Purchased Times")
        return purchasedNumber;
    }

    async loadSongDetails() {
        console.log("Entering loadSongDetails function");
        const contractInstance = await this.props.contract.deployed();
        console.log("Contract instance deployed successfully");
        let songInfoList = [];
        console.log('Number of song IDs to process:', this.state.songIDs.length);

    
        for (let i = 0; i < this.state.songIDs.length; i++) {
            console.log(`Processing song ID at index ${i}:`, this.state.songIDs[i]);
            let songDetails = await contractInstance.getSongDetails(this.state.songIDs[i], { from: this.props.account });
            console.log("Song Details fetched for ID:", songDetails[0]);
            console.log("Sending song ID to loadPurchasedTimes:", this.state.songIDs[i]);
            let songPurchasedNumber = await this.loadPurchasedTimes(this.state.songIDs[i]);
            console.log('Purchased times for song ID', this.state.songIDs[i], ':', songPurchasedNumber.toString());
    
            // Extract the artist ID from songDetails[8] and convert it to a string if necessary

            let artistIDString = songDetails[8].toString(); // This is your string "[1,2]"
            console.log('artistIDString\n',artistIDString); // This should log [1, 2]

            let artistIDArray = artistIDString.split(',').map(Number); // This converts the string to an array
            console.log('artistIDArray\n',artistIDArray, '\n', typeof artistIDArray); // This should log [1, 2]
            let test_array=[2];
            let test2=test_array.toString();
            console.log('test_array\n',test_array, '\n', typeof test_array)

            let publisherID = songDetails[1].toString();
            console.log("PublisherID:\n", songDetails[1].toString())

            // let artistID = songDetails[8].toString();
            // console.log("Extracted ArtistID:", artistID);
    
            // Call the getArtistNameByID function to get the artist name
            let artistNames = [];
            for (let i = 0; i < artistIDArray.length; i++){
                console.log('Artist ID:', artistIDArray[i]);
                let artistName = await contractInstance.getArtistNameByID(artistIDArray[i], { from: this.props.account });
                console.log('Artist name fetched:', artistName);
                artistNames.push(artistName);

            }
    
            songInfoList.push({
                'name': songDetails[2],
                'genre': songDetails[7],
                'hash': songDetails[6],
                'cost_personal': songDetails[3].toString(),
                'cost_commercial': songDetails[4].toString(),
                'artistsIDSong': songDetails[8].toString(),
                'timesPurchased': songPurchasedNumber.toString(),
                'artist_name': artistNames
            });
            // this.state.purchased_times += parseInt(songPurchasedNumber);
            this.setState(prevState => ({
            purchased_times: prevState.purchased_times + parseInt(songPurchasedNumber),
            songs: songInfoList
        }));
            // console.
        }
        console.log("Total purchased times:", this.state.purchased_times);
        console.log("Artist Songs:", songInfoList.artistsIDSong);
        console.log("Song Info List:", songInfoList);
    
        this.setState({ songs: songInfoList });
        console.log("State updated with songInfoList");
    }
    
    openForm(){
        this.setState({form : true})
    }

    closeForm(){
        this.setState({form : false})
    }

    handleSearchChange(event) {
        this.setState({ searchQuery: event.target.value });
    }

    render() {
        if (this.state.publisherID === "") {
            return (
                <div style={styles.main}>
                    <h1>Hello</h1>
                    <Loader type="Bars" color={COLORS.black} />
                </div>
            );
        } else {
            // Filter songs based on the search query
            const filteredSongs = this.state.songs.filter(song =>
                song.name.toLowerCase().includes(this.state.searchQuery.toLowerCase())
            );

            return (
                <div style={styles.main}>
                    <div style={styles.info}>
                        <h1><FontAwesomeIcon icon={faBuilding} /> {this.state.name} </h1>
                        <h3> Publisher ID : {this.state.publisherID} </h3>
                        <h3> Total Songs Sold : {this.state.purchased_times} </h3>
                    </div>
                    <div style={styles.box}>
                        {/* Search bar styled with Bootstrap */}
                        <input
                            type="text"
                            placeholder="Search by song name..."
                            value={this.state.searchQuery}
                            onChange={this.handleSearchChange}
                            className="form-control mb-3" // Bootstrap classes for styling
                        />
                        {filteredSongs.map((item, i) => (
                            <SongCard
                                type={"artist"}
                                name={item.name}
                                genre={item.genre}
                                cost={item.cost_personal}
                                likes={item.timesPurchased}
                                hash={item.hash}
                                key={i}
                            />
                        ))}
                    </div>
                    <h1><FontAwesomeIcon icon={faPlusSquare} onClick={() => { this.openForm() }} /></h1>
                    <AddSongCard contract={this.props.contract} ipfs={this.props.ipfs} account={this.props.account} form={this.state.form} closeForm={this.closeForm} />
                </div>
            );
        }
    }
}


  
const styles = {
    main : {
        display:"flex", 
        flexDirection:"column", 
        justifyContent: "center", 
        alignItems: "center", 
        height:"100vh",
        gap :"5%",
        background:COLORS.white,
        },
    info : {
        display:"flex", 
        flexDirection:"column", 
        alignItems: "center", 
        },
    box : {
        height :"50%",
        width :"60%",
        padding :"2%",
        display:"flex", 
        flexDirection:"column",
        borderRadius:"30px",
        border: "3px solid",
        overflow: "auto",
        gap :"2%",
        boxShadow: "2px 5px 2px #191919",
        borderColor :COLORS.black,
        backgroundColor:COLORS.white,
    },
}
  
export default Publisher;
