import React from "react";
import Popup from 'reactjs-popup';
import Loader from "react-loader-spinner";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faMusic, faFileUpload } from '@fortawesome/free-solid-svg-icons';
import Web3 from 'web3';
import axios from 'axios';

class AddSongCard extends React.Component {
    constructor(props) {
        super(props);
        this.web3 = new Web3(Web3.givenProvider || "http://localhost:7545");
        this.state = { 
            name: "", 
            genre: "", 
            cost_personal: "", 
            cost_commercial: "", 
            artist_ids: [], 
            royalty_percent: '', 
            buffer: "", 
            loading: false, 
            artistSearchQuery: "", // New state variable for artist search query
            artists: [] // New state variable to store all artists
        };
        this.handleArtistSearchChange = this.handleArtistSearchChange.bind(this);
    }

    componentDidMount() {
        this.loadArtists();
    }

    async loadArtists() {
        try {
            const contractInstance = await this.props.contract.deployed();
            const [artistIDs, artistNames] = await contractInstance.getAllArtists.call({ from: this.props.account });
            const artists = artistIDs.map((id, index) => ({ id, name: artistNames[index] }));
            this.setState({ artists });
        } catch (error) {
            console.error("Failed to load artists:", error);
        }
    }

    handleArtistSearchChange(event) {
        this.setState({ artistSearchQuery: event.target.value });
    }

    render() {
        const filteredArtists = this.state.artists.filter(artist =>
            artist.name.toLowerCase().includes(this.state.artistSearchQuery.toLowerCase())
        );

        return (
            <Popup
                open={this.props.form}
                onClose={() => { this.props.closeForm() }}
                modal lockScroll repositionOnResize
            >
                {this.state.loading
                    ? <div style={styles.load}> <Loader type="Bars" color={COLORS.black} /></div>
                    : <form>
                        <h2 style={{ textAlign: "center" }}> <FontAwesomeIcon icon={faMusic} /> Add Song </h2>
                        <div style={styles.form} >
                            {/* name */}
                            <input type="text" style={styles.textInput}
                                placeholder="Name"
                                value={this.state.name} required
                                onChange={(x) => { this.setState({ name: x.target.value }) }} />
                            {/* genre */}
                            <input type="text" style={styles.textInput}
                                placeholder="Genre"
                                value={this.state.genre} required
                                onChange={(x) => { this.setState({ genre: x.target.value }) }} />
                            {/* cost Personal */}
                            <input type="number" style={styles.textInput}
                                placeholder="cost_personal"
                                value={this.state.cost_personal} required
                                onChange={(x) => { this.setState({ cost_personal: x.target.value }) }} />
                            {/* cost commercial */}
                            <input type="number" style={styles.textInput}
                                placeholder="cost_commercial"
                                value={this.state.cost_commercial} required
                                onChange={(x) => { this.setState({ cost_commercial: x.target.value }) }} />
                            {/* royalty percent */}
                            <input type="number" style={styles.textInput}
                                placeholder="royalty_percent"
                                value={this.state.royalty_percent} required
                                onChange={(x) => { this.setState({ royalty_percent: x.target.value }) }} />
                            {/* artist ids */}
                            <input type="text" style={styles.textInput}
                                placeholder="Aritst ID"
                                value={this.state.artist_ids}
                                onChange={(x) => { this.setState({ artist_ids: x.target.value }) }} />
                            {/* Artist search */}
                            <input
                                type="text"
                                placeholder="Search artist..."
                                value={this.state.artistSearchQuery}
                                onChange={this.handleArtistSearchChange}
                            />
                            <div>
                                {filteredArtists.map(artist => (
                                    <div key={artist.id}>{artist.name} (ID: {artist.id})</div>
                                ))}
                            </div>
                            <label htmlFor="upload"> <FontAwesomeIcon icon={faFileUpload} size="3x" /> </label>
                            <input type="file" style={{ display: 'none' }} id="upload" required
                                onChange={this.captureFile}>
                            </input>
                        </div>
                        <input type="submit" onClick={this.onSubmitClick} style={styles.button} value="Publish" />
                    </form>
                }
            </Popup>
        );
    }
}

const styles = {
    load: {
        height: "100%",
        display: "flex",
        flexDirection: "column",
        justifyContent: "space-around",
        alignItems: "center",
        background: COLORS.white
    },
    form: {
        marginBottom: "5%",
        marginTop: "5%",
        padding: "5%",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        borderRadius: "10px",
        border: "2px solid",
        overflow: "auto",
        gap: "20px",
        borderColor: COLORS.black,
        backgroundColor: COLORS.white,
    },
    textInput: {
        width: "100%",
        borderRadius: "15px",
        padding: "3%",
    },
    button: {
        width: "50%",
        marginLeft: "25%",
        padding: "3%",
        borderRadius: "15px",
        border: "0px",
        cursor: "pointer",
        background: COLORS.black,
        color: COLORS.white,
    },
}

export default AddSongCard;
