import React from 'react';
import Web3 from 'web3';
import {COLORS} from "./Colors";
import Loader from "react-loader-spinner";
import Library from "./Library";
import Store from "./Store";

class Personal extends React.Component {
    constructor(props) {
        super(props);
        this.web3 = new Web3(Web3.givenProvider || "http://localhost:7545");
        this.state = {
            name: "",
            personalID: "",
            store: [],
            library: [],
            songsMapping: {},
            searchTerm: "",
            activePage: "library" // Default active page
        };
    }

    // Your existing methods (loadStore, loadPersonalDetails, loadSongDetails) remain unchanged
componentDidMount() {
        this.loadStore().then(() => {
            console.log("Loaded Store");
            this.loadPersonalDetails().then(() => {
                console.log("Loaded Audience's Details");
                this.loadSongDetails().then(() => {
                    console.log("Loaded Songs");
                });
            });
        });
    }

    async loadStore() {
        try {
            const contractInstance = await this.props.contract.deployed();
            const count = await contractInstance.getNumSongs({ from: this.props.account });
            const totalSongs = parseInt(count.toString()) + 1;
            let mapping = {};
            for (let i = 1; i <= totalSongs; i++) {
                mapping[i] = 0;
            }
            this.setState({
                songsMapping: mapping
            });
        } catch (error) {
            console.error("Error loading store:", error);
        }
    }

    async loadPersonalDetails() {
        try {
            const contractInstance = await this.props.contract.deployed();
            const personalDetails = await contractInstance.getPersonalDetails({ from: this.props.account });
            let mapping = this.state.songsMapping;
            for (let i = 0; i < personalDetails[3].length; i++) {
                mapping[parseInt(personalDetails[3][i].toString())] = 1;
            }
            this.setState({
                name: personalDetails[2].toString(),
                personalID: personalDetails[0].toString(),
                songsMapping: mapping
            });
        } catch (error) {
            console.error("Error loading audience details:", error);
        }
    }

    async loadSongDetails() {
        try {
            const contractInstance = await this.props.contract.deployed();
            let n = Object.keys(this.state.songsMapping).length;
            let newSongs = [];
            let purchasedSongs = [];

            for (let i = 1; i <= n; i++) {
                let songDetails = await contractInstance.getSongDetails(i, { from: this.props.account });
                if (this.state.songsMapping[i] === 1) {
                    purchasedSongs.push({
                        'name': songDetails[2].toString(),
                        'artist': songDetails[1].toString(),
                        'genre': songDetails[7].toString(),
                        'hash': songDetails[6].toString(),
                        'cost': songDetails[3].toString(),
                        'songID': songDetails[0].toString()
                    });
                } else {
                    newSongs.push({
                        'name': songDetails[2].toString(),
                        'artist': songDetails[1].toString(),
                        'genre': songDetails[7].toString(),
                        'hash': songDetails[6].toString(),
                        'cost': songDetails[3].toString(),
                        'songID': songDetails[0].toString()
                    });
                }
            }

            this.setState({
                library: purchasedSongs,
                store: newSongs
            });
        } catch (error) {
            console.error("Error loading song details:", error);
        }
    }

    handlePageChange = (page) => {
        this.setState({ activePage: page });
    }

    render() {
        if (this.state.personalID === "") {
            return (
                <div style={styles.main}>
                    <Loader type="Bars" color={COLORS.black} />
                </div>
            );
        } else {
            return (
                <div style={styles.main}>
                    <div style={styles.info}>
                        <h2><FontAwesomeIcon icon={faHeadphones} />{this.state.name}</h2>
                        <h3>Personal ID: {this.state.personalID}</h3>
                    </div>
                    <button onClick={() => this.handlePageChange("library")}>Library</button>
                    <button onClick={() => this.handlePageChange("store")}>Store</button>
                    {this.state.activePage === "library" && (
                        <Library library={this.state.library} />
                    )}
                    {this.state.activePage === "store" && (
                        <Store
                            contract={this.props.contract}
                            ipfs={this.props.ipfs}
                            account={this.props.account}
                            store={this.state.store}
                            searchTerm={this.state.searchTerm}
                            onSearchTermChange={(term) => this.setState({ searchTerm: term })}
                            styles={styles}
                        />
                    )}
                </div>
            );
        }
    }
}

// Your styles object remains unchanged
    const styles = {
     main: {
        padding: "3%",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
        height: "110vh",
        gap: "5%",
        background: COLORS.white,
     },
     info: {
        marginBottom: "20px",
        textAlign: "center",
     },
     library: {
        width: "90%",
        padding: "25px",
        borderRadius: "30px",
        border: "3px solid",
        overflow: "auto",
        gap: "10px",
        boxShadow: "2px 5px 2px #191919",
        borderColor: COLORS.black,
        backgroundColor: COLORS.white,
     },
     store: {
        width: "90%",
        marginTop: "20px",
        padding: "25px",
        borderRadius: "30px",
        border: "3px solid",
        overflow: "auto",
        gap: "10px",
        boxShadow: "2px 5px 2px #191919",
        borderColor: COLORS.black,
        backgroundColor: COLORS.white,
     },
     searchInput: {
        width: "100%",
        padding: "10px",
        marginBottom: "10px",
        borderRadius: "5px",
        border: "1px solid #ccc",
     },
    };

export default Personal;
