import Web3 from 'web3'
import React from "react"

import {COLORS} from "./Colors"
import SongCard from "./SongCard"
import Loader from "react-loader-spinner"
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faHeadphones, faStore, faUser, faGift } from '@fortawesome/free-solid-svg-icons'


class Personal extends React.Component {

    constructor(props){
      super(props)
      this.web3 = new Web3(Web3.givenProvider || "http://localhost:7545")
      this.state = {name : "", personalID: "", store : [], library: [], songsMapping: {}, supportArtistUsername:"", donation:""}
    }
  
    componentDidMount() {
        this.loadStore().then(() => {
            console.log("Loaded Store");
            this.loadAudienceDetails().then(() => {
                console.log("Loaded Audience's Details");
                this.loadSongDetails().then(() => {
                    console.log("Loaded Songs");
                });
            });
        });
    }
    
    async loadStore() {
        try {
            const contractInstance = await this.props.contract.deployed();
            console.log("contract:", contractInstance);
            const count = await contractInstance.getNumSongs({ from: this.props.account });
            console.log(count);
            let mapping = {};
            for (let i = 1; i <= parseInt(count.toString()) + 1; i++) {
                mapping[i] = 0;
            }
            this.setState({ songsMapping: mapping });
        } catch (error) {
            console.error("Error loading store:", error);
        }
    }
    
    async loadAudienceDetails() {
        try {
            const contractInstance = await this.props.contract.deployed();
            const personalDetails = await contractInstance.getPersonalDetails({ from: this.props.account });
            let mapping = this.state.songsMapping;
            for (let i = 0; i < personalDetails[3].length; i++) {
                mapping[parseInt(personalDetails[3][i].toString())] = 1;
            }
            this.setState({
                name: personalDetails[2].toString(),
                personalID: personalDetails[0].toString(),
                songsMapping: mapping
            });
        } catch (error) {
            console.error("Error loading audience details:", error);
        }
    }
    
    async loadSongDetails() {
        try {
            const contractInstance = await this.props.contract.deployed();
            let n = Object.keys(this.state.songsMapping).length;
            let newSongs = [];
            let purchasedSongs = [];
    
            for (let i = 1; i <= n; i++) {
                let songDetails = await contractInstance.getSongDetails(i, { from: this.props.account });
                if (this.state.songsMapping[i] === 1) {
                    purchasedSongs.push({
                        'name': songDetails[2],
                        'artist': songDetails[1],
                        'genre': songDetails[6],
                        'hash': songDetails[7],
                        'cost': songDetails[3].toString(),
                        'songID': songDetails[0]
                    });
                } else {
                    newSongs.push({
                        'name': songDetails[2],
                        'artist': songDetails[1],
                        'genre': songDetails[6],
                        'hash': songDetails[7],
                        'cost': songDetails[3].toString(),
                        'songID': songDetails[0]
                    });
                }
            }
    
            this.setState({
                library: purchasedSongs,
                store: newSongs
            });
        } catch (error) {
            console.error("Error loading song details:", error);
        }
    }

    onSubmitClick = async (event)=>{
      event.preventDefault()
      if(this.state.supportArtistUsername){
          const contractInstance = await this.props.contract.deployed()
          await contractInstance.donateArtist(this.state.supportArtistUsername, {from:this.props.account, value:this.web3.utils.toWei(this.state.donation, 'milliether')}).then(()=>{
              window.location.reload()
          })
      }
      console.log("Submitted")
    } 


    render(){
      
      if (this.state.ID === ""){
          return (
              <div style = {styles.main}>
                <Loader type = "Bars" color = {COLORS.black}/>
              </div>
          )
      }

    else{
        return (
            <div style={styles.main}>
            <div style={styles.info}>
                <h2><FontAwesomeIcon icon={faHeadphones}/>{this.state.name}  </h2>
                <h3> Personal ID : {this.state.personalID} </h3>
            </div>
            <div style={styles.library}>
                <h3 style={{ textAlign: "center" }}> <FontAwesomeIcon icon={faUser}/> Library </h3>
                {this.state.library.map((item, i) => (
                    <SongCard
                        type={"audience"}
                        name={item.name}
                        artist={item.artist}
                        genre={item.genre}
                        hash={item.hash}
                        songID={item.songID}
                        key={i} />
                ))}
            </div>
            <div style={styles.store}>
                <h3 style={{ textAlign: "center" }}> <FontAwesomeIcon icon={faStore}/> Store </h3>
                {this.state.store.map((item, i) => (
                    <SongCard
                        contract={this.props.contract} ipfs={this.props.ipfs} account={this.props.account}
                        type={"shop"}
                        name={item.name}
                        artist={item.artist}
                        genre={item.genre}
                        cost={item.cost}
                        hash={item.hash}
                        songID={item.songID}
                        key={i} />
                ))}
            </div>
        </div>
        )
    }
  }
}
  
const styles = {
    main: {
        padding: "3%",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
        height: "110vh",
        gap: "5%",
        background: COLORS.brown,
    },
    info: {
        marginBottom: "20px",
        textAlign: "center",
    },
    library: {
        width: "90%",
        padding: "25px",
        borderRadius: "30px",
        border: "3px solid",
        overflow: "auto",
        gap: "10px",
        boxShadow: "2px 5px 2px #191919",
        borderColor: COLORS.black,
        backgroundColor: COLORS.white,
    },
    store: {
        width: "90%",
        marginTop: "20px", // Add margin to separate library and store
        padding: "25px",
        borderRadius: "30px",
        border: "3px solid",
        overflow: "auto",
        gap: "10px",
        boxShadow: "2px 5px 2px #191919",
        borderColor: COLORS.black,
        backgroundColor: COLORS.white,
    },
  area : {
    height :"100%",
    width :"90%",
    display:"flex", 
    flexDirection:"row",
    gap : "5%"
  },
  form:{
    padding :"2%",
    display:"flex", 
    flexDirection:"row",
    alignItems: "center", 
    borderRadius:"10px",
    border: "2px solid",
    gap : "3%",
    borderColor :COLORS.black,
    backgroundColor:COLORS.brown,
  },
  textInput : {
    width : "50%",
    borderRadius:"15px",
    padding: "2%",
  },
  button : {
    padding : "2%",
    borderRadius:"15px", 
    border:"0px", 
    cursor:"pointer",
    background:COLORS.black, 
    color: COLORS.white,
  },
}
  
export default Personal;
