import React from "react";
import { COLORS } from "./Colors";
import Popup from 'reactjs-popup';
import Loader from "react-loader-spinner";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faMusic, faFileUpload } from '@fortawesome/free-solid-svg-icons';
import Web3 from 'web3';
import axios from 'axios';

class AddSongCard extends React.Component {
  constructor(props) {
    super(props);
    this.web3 = new Web3(Web3.givenProvider || "http://localhost:7545");
    this.state = { 
      name: "", 
      genre: "", 
      cost_personal: "", 
      cost_commercial: "", 
      artist_ids: [], 
      royalty_percent: '', 
      buffer: "", 
      loading: false, 
      allArtists: [], // Store all artists fetched from the blockchain
      filteredArtists: [], // Store filtered artists based on search
    };
  }

  async componentDidMount() {
    // Fetch all artists from the blockchain when the component mounts
    await this.fetchAllArtists();
  }

  async fetchAllArtists() {
    try {
      // Ensure the contract instance is available and has the getAllArtists method
      if (!this.props.contract || !this.props.contract.methods.getAllArtists) {
        throw new Error("Contract instance or getAllArtists method not available");
      }
  
      // Call the smart contract method to get all artists
      const allArtistsCount = await this.props.contract.methods.nextArtistID().call();
      const allArtists = [];
      for (let i = 1; i < allArtistsCount; i++) {
        const artistName = await this.props.contract.methods.artists(i).call();
        allArtists.push({ artistID: i, artistName });
      }
  
      // Update state with fetched artists
      this.setState({ allArtists, filteredArtists: allArtists });
    } catch (error) {
      console.error("Error fetching artists:", error);
    }
  }

  handleSearch = (event) => {
    const searchQuery = event.target.value.toLowerCase();
    // Filter artists based on the search query
    const filteredArtists = this.state.allArtists.filter(artist => artist.artistName.toLowerCase().includes(searchQuery));
    this.setState({ filteredArtists });
  }

  handleArtistSelection = (artistID, artistName) => {
    // Update artist ID and name in the form fields
    this.setState({ artist_ids: [artistID], name: artistName });
  }

  captureFile = (event) => {
    event.preventDefault();
    const file = event.target.files[0];
    const file_reader = new FileReader();
    file_reader.readAsArrayBuffer(file);
    file_reader.onloadend = () => {
      this.setState({ buffer: Buffer.from(file_reader.result) });
    };
  }

  onSubmitClick = async (event) => {
    event.preventDefault();

    if (this.state.buffer) {
      this.setState({ loading: true });

      const formData = new FormData();
      formData.append('file', new Blob([this.state.buffer]));

      try {
        const JWT = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..oqfWb_f78GtF7VXqrrmNfbYP8UFzx6oVGvutjFLRh_g';

        const pinataResponse = await axios.post("https://api.pinata.cloud/pinning/pinFileToIPFS", formData, {
          maxBodyLength: 'Infinity',
          headers: {
            'Content-Type': 'multipart/form-data',
            'Authorization': `Bearer ${JWT}`,
          },
        });
        const songHash = pinataResponse.data.IpfsHash;

        // Interact with the smart contract
        const contractInstance = await this.props.contract.deployed();
        console.log("Artists of songs:",this.state.artist_ids)
        let artistIDArray=this.state.artist_ids.split(',').map(Number);
        await contractInstance.addSong(
          this.state.name,
          this.web3.utils.toWei(this.state.cost_personal, 'milliether'),
          this.web3.utils.toWei(this.state.cost_commercial, 'milliether'),
          artistIDArray,
          parseInt(this.state.royalty_percent),
          songHash,
          this.state.genre,
          { from: this.props.account });

        // Reset loading state and other form fields if needed...
        this.setState({ loading: false });
        this.props.closeForm();
        window.location.reload();
        console.log("song added")

      } catch (error) {
        console.error('Error pinning to IPFS:', error);
        this.setState({ loading: false });
      }
    }
  }

  render() {
    return (
      <Popup
        open={this.props.form}
        onClose={() => { this.props.closeForm() }}
        modal lockScroll repositionOnResize
        contentStyle={styles.contentStyle}
        overlayStyle={styles.overlayStyle}>
        {this.state.loading
          ? <div style={styles.load}> <Loader type="Bars" color={COLORS.black} /></div>
          : <form>
            <h2 style={{ textAlign: "center" }}> <FontAwesomeIcon icon={faMusic} /> Add Song </h2>
            <div style={styles.form} >
              {/* Search bar for artist */}
              <input type="text" style={styles.textInput}
                placeholder="Search Artist"
                onChange={this.handleSearch} />
              {/* Display filtered artists */}
              {this.state.filteredArtists.map(artist => (
                <div key={artist.artistID} onClick={() => this.handleArtistSelection(artist.artistID, artist.artistName)}>
                  {artist.artistName}
                </div>
              ))}
              {/* Remaining form fields */}
              {/* genere */}
              <input type="text" style={styles.textInput}
                placeholder="Genre"
                value={this.state.genre} required
                onChange={(x) => { this.setState({ genre: x.target.value }) }} />
              {/* cost Personal */}
              <input type="number" style={styles.textInput}
                placeholder="cost_personal"
                value={this.state.cost_personal} required
                onChange={(x) => { this.setState({ cost_personal: x.target.value }) }} />
              {/* cost commercial */}
              <input type="number" style={styles.textInput}
                placeholder="cost_commercial"
                value={this.state.cost_commercial} required
                onChange={(x) => { this.setState({ cost_commercial: x.target.value }) }} />
              {/* royalty percent */}
              <input type="number" style={styles.textInput}
                placeholder="royalty_percent"
                value={this.state.royalty_percent} required
                onChange={(x) => { this.setState({ royalty_percent: x.target.value }) }} />
              {/* artist ids */}
              <input type="text" style={styles.textInput}
                placeholder="Artist ID"
                value={this.state.artist_ids}
                readOnly
                onChange={(x) => { this.setState({ artist_ids: x.target.value }) }} />
              <label htmlFor="upload"> <FontAwesomeIcon icon={faFileUpload} size="3x" /> </label>
              <input type="file" style={{ display: 'none' }} id="upload" required
                onChange={this.captureFile}>
              </input>
            </div>
            <input type="submit" onClick={this.onSubmitClick} style={styles.button} value="Publish" />
          </form>
        }
      </Popup>
    );
  }
}

const styles = {
  load: {
    height: "100%",
    display: "flex",
    flexDirection: "column",
    justifyContent: "space-around",
    alignItems: "center",
    background: COLORS.white
  },
  contentStyle: {
    height: "90%",
    width: "40%",
    padding: "1%",
    display: "flex",
    flexDirection: "column",
    justifyContent: "space-between",
    borderRadius: "15px",
    border: "2px solid",
    overflow: "auto",
    borderColor: COLORS.black,
    backgroundColor: "#FFFFFF",
  },
  overlayStyle: {
    backgroundColor: COLORS.blurBlack
  },
  arrowStyle: {
  },
  form: {
    marginBottom: "5%",
    marginTop: "5%",
    padding: "5%",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    borderRadius: "10px",
    border: "2px solid",
    overflow: "auto",
    gap: "20px",
    borderColor: COLORS.black,
    backgroundColor: COLORS.white,
  },
  textInput: {
    width: "100%",
    borderRadius: "15px",
    padding: "3%",
  },
  button: {
    width: "50%",
    marginLeft: "25%",
    padding: "3%",
    borderRadius: "15px",
    border: "0px",
    cursor: "pointer",
    background: COLORS.black,
    color: COLORS.white,
  },
}

export default AddSongCard;
