import React from "react";
import { COLORS } from "./Colors";
import Popup from 'reactjs-popup';
import Loader from "react-loader-spinner";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faMusic, faFileUpload } from '@fortawesome/free-solid-svg-icons';
import Web3 from 'web3';
import axios from 'axios';

class AddSongCard extends React.Component {
  constructor(props) {
    super(props);
    this.web3 = new Web3(Web3.givenProvider || "http://localhost:7545");
    this.state = {
      name: "",
      genre: "",
      cost_personal: "",
      cost_commercial: "",
      artist_ids: "",
      royalty_percent: '',
      buffer: "",
      loading: false,
      searchInput: "",
      filteredArtists: [],
      allArtists: [],
    };
  }

  componentDidMount = async () => {
    try {
       const contractInstance = await this.props.contract.deployed();
       const [artistIDs, artistNames] = await contractInstance.getAllArtists.call();
       if (artistIDs && artistNames) {
         this.setState({ allArtists: artistNames.map((name, index) => ({ id: artistIDs[index], name })) });
       } else {
         console.error('Failed to fetch artists from the smart contract.');
       }
    } catch (error) {
       console.error('Error fetching artists:', error);
    }
   };

  handleSearchInput = (event) => {
    const searchInput = event.target.value;
    const filteredArtists = this.state.allArtists.filter(artist =>
      artist.name.toLowerCase().includes(searchInput.toLowerCase())
    );
    this.setState({ searchInput, filteredArtists });
  };

  selectArtist = (artistId) => {
    this.setState({ artist_ids: artistId.toString(), searchInput: "", filteredArtists: [] });
  };

  captureFile = (event) => {
    event.preventDefault();
    const file = event.target.files[0];
    const file_reader = new FileReader();
    file_reader.readAsArrayBuffer(file);
    file_reader.onloadend = () => {
      this.setState({ buffer: Buffer.from(file_reader.result) });
    };
  }

  onSubmitClick = async (event) => {
    event.preventDefault();

    if (this.state.buffer) {
      this.setState({ loading: true });

      const formData = new FormData();
      formData.append('file', new Blob([this.state.buffer]));

      try {
        const JWT = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..oqfWb_f78GtF7VXqrrmNfbYP8UFzx6oVGvutjFLRh_g';

        const pinataResponse = await axios.post("https://api.pinata.cloud/pinning/pinFileToIPFS", formData, {
          maxBodyLength: 'Infinity',
          headers: {
            'Content-Type': 'multipart/form-data',
            'Authorization': `Bearer ${JWT}`,
          },
        });
        const songHash = pinataResponse.data.IpfsHash;

        const contractInstance = await this.props.contract.deployed();
        let artistIDArray = this.state.artist_ids.split(',').map(Number);
        await contractInstance.addSong(
          this.state.name,
          this.web3.utils.toWei(this.state.cost_personal, 'milliether'),
          this.web3.utils.toWei(this.state.cost_commercial, 'milliether'),
          artistIDArray,
          parseInt(this.state.royalty_percent),
          songHash,
          this.state.genre,
          { from: this.props.account });

        this.setState({ loading: false });
        this.props.closeForm();
        window.location.reload();
        console.log("song added")

      } catch (error) {
        console.error('Error pinning to IPFS:', error);
        this.setState({ loading: false });
      }
    }
  }

  render() {
    return (
      <Popup
        open={this.props.form}
        onClose={() => { this.props.closeForm() }}
        modal
        lockScroll
        repositionOnResize
        contentStyle={styles.contentStyle}
        overlayStyle={styles.overlayStyle}>
        {this.state.loading
          ? <div style={styles.load}> <Loader type="Bars" color={COLORS.black} /></div>
          : <form>
            <h2 style={{ textAlign: "center" }}> <FontAwesomeIcon icon={faMusic} /> Add Song </h2>
            <div style={styles.form} >
              {/* Search bar */}
              <input
                type="text"
                placeholder="Search for an artist"
                value={this.state.searchInput}
                onChange={this.handleSearchInput}
                style={styles.textInput}
              />

              {/* Search results */}
              {this.state.filteredArtists.map(artist => (
                <div key={artist.id} onClick={() => this.selectArtist(artist.id)} style={styles.searchResult}>
                  {artist.name} - ID: {artist.id}
                </div>
              ))}

              {/* Name */}
              <input type="text"
                placeholder="Name"
                value={this.state.name}
                onChange={(e) => this.setState({ name: e.target.value })}
                style={styles.textInput}
                required />

              {/* Genre */}
              <input type="text"
                placeholder="Genre"
                value={this.state.genre}
                onChange={(e) => this.setState({ genre: e.target.value })}
                style={styles.textInput}
                required />

              {/* Cost Personal */}
              <input type="number"
                placeholder="Cost Personal"
                value={this.state.cost_personal}
                onChange={(e) => this.setState({ cost_personal: e.target.value })}
                style={styles.textInput}
                required />

              {/* Cost Commercial */}
              <input type="number"
                placeholder="Cost Commercial"
                value={this.state.cost_commercial}
                onChange={(e) => this.setState({ cost_commercial: e.target.value })}
                style={styles.textInput}
                required />

              {/* Royalty Percent */}
              <input type="number"
                placeholder="Royalty Percent"
                value={this.state.royalty_percent}
                onChange={(e) => this.setState({ royalty_percent: e.target.value })}
                style={styles.textInput}
                required />

              {/* Artist IDs */}
              <input type="text"
                placeholder="Artist ID"
                value={this.state.artist_ids}
                onChange={(e) => this.setState({ artist_ids: e.target.value })}
                style={styles.textInput}
                required />

              {/* File Upload */}
              <label htmlFor="upload"> <FontAwesomeIcon icon={faFileUpload} size="3x" /> </label>
              <input type="file"
                style={{ display: 'none' }}
                id="upload"
                required
                onChange={this.captureFile} />
            </div>
            <input type="submit" onClick={this.onSubmitClick} style={styles.button} value="Publish" />
          </form>
        }
      </Popup>
    );
  }
}


const styles = {
  load: {
    height: "100%",
    display: "flex",
    flexDirection: "column",
    justifyContent: "space-around",
    alignItems: "center",
    background: COLORS.white
  },
  contentStyle: {
    height: "90%",
    width: "40%",
    padding: "1%",
    display: "flex",
    flexDirection: "column",
    justifyContent: "space-between",
    borderRadius: "15px",
    border: "2px solid",
    overflow: "auto",
    borderColor: COLORS.black,
    backgroundColor: "#FFFFFF",
  },
  overlayStyle: {
    backgroundColor: COLORS.blurBlack
  },
  arrowStyle: {
  },
  form: {
    marginBottom: "5%",
    marginTop: "5%",
    padding: "5%",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    borderRadius: "10px",
    border: "2px solid",
    overflow: "auto",
    gap: "20px",
    borderColor: COLORS.black,
    backgroundColor: COLORS.white,
  },
  textInput: {
    width: "100%",
    borderRadius: "15px",
    padding: "3%",
  },
  button: {
    width: "50%",
    marginLeft: "25%",
    padding: "3%",
    borderRadius: "15px",
    border: "0px",
    cursor: "pointer",
    background: COLORS.black,
    color: COLORS.white,
  },
}

export default AddSongCard;
