import React from "react"
import Artist from "./Artist"
import Audience from "./Audience"
import CommercialEntity from "./CommercialEntity"
import {COLORS} from "./Colors"
import Logo from "../Assets/logo.png"
import contractMeta from "../Build/blockstudio.json"

import Web3 from 'web3'
import contract from 'truffle-contract'
import { create } from 'ipfs-http-client';
import axios from 'axios';
import Loader from "react-loader-spinner"
import SwitchSelector from "react-switch-selector"
class Login extends React.Component {

  constructor(props){
    super(props)
      this.web3 = new Web3(Web3.givenProvider || "http://localhost:7545")
      this.contract = contract(contractMeta)
      this.contract.setProvider(this.web3.currentProvider)
      this.state = { account : "", ipfs : "", username: "", type : "", choice : "1", }
    }

  componentDidMount(){
    this.loadBlockchain().then(() => console.log("Loaded Blockchain"))
    this.loadIPFS().then(() => console.log("Loaded IPFS"))
    this.loginUser().then(() => console.log("Login Successful"))
  }

  async loadBlockchain(){
    const accounts = await this.web3.eth.getAccounts()
    this.setState({account:accounts[0]})
  }

  async loadIPFS() {
    try {
        // Retrieve Pinata JWT token from a secure source (environment variable, etc.)
        const JWT = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..oqfWb_f78GtF7VXqrrmNfbYP8UFzx6oVGvutjFLRh_g';

        // Fetch Pinata IPFS gateway URL
        const response = await axios.get('https://api.pinata.cloud/data/pinList', {
            headers: {
                'Authorization': `Bearer ${JWT}`
            }
        });

        const ipfsGatewayUrl = response.data.rows[0].ipfs_pin_hash;

        // Create IPFS client using Pinata gateway
        const conn = create({ host: ipfsGatewayUrl, port: '5001', protocol: 'https' });

        this.setState({ ipfs: conn });
    } catch (error) {
        console.error('Error loading IPFS:', error);
    }
}

loginUser = async () => {
  try {
    const contractInstance = await this.contract.deployed();
    const userType = await contractInstance.checkUser({ from: this.state.account });
    this.setState({ type: userType.toString() });
  } catch (error) {
    this.setState({ error: error.message });
  }
}

registerUser = async () =>{
  try {
    const contractInstance = await this.contract.deployed();
    const { username, account, choice } = this.state;
    switch (choice) {
      case "1":
        await contractInstance.addNewArtist(username, { from: account });
        break;
      case "2":
        await contractInstance.addNewAudience(username, { from: account });
        break;
      case "3":
        await contractInstance.addNewCommercialEntity(username, { from: account });
        break;
      default:
        break;
    }
    await this.loginUser();
  } catch (error) {
    this.setState({ error: error.message });
  }
}

render(){
  const { loading, error, type, account, ipfs, username, choice } = this.state;

  if (loading) {
    return (
      <div style={styles.main}>
        <Loader type="Bars" color={COLORS.black} />
      </div>
    );
  }

  if (error) {
    return (
      <div style={styles.main}>
        <p>Error: {error}</p>
      </div>
    );
  }

  if (type === "0" ) {
    return (
      <div style={styles.main}>
        <img style={styles.img} alt="logo" src={Logo} />
        <div style={styles.switch}>
          <SwitchSelector
            onChange={(val) => {this.setState({choice: val})}}
            options={[
              {label: "Artist ðŸŽ™", value: "1", selectedBackgroundColor: "#26ae5f"},
              {label: "Audience ðŸŽ§", value: "2", selectedBackgroundColor: "#26ae5f"},
              {label: "Commercial Entity", value: "3", selectedBackgroundColor: "#26ae5f"}
            ]}
            wrapperBorderRadius={50}
            optionBorderRadius={50}
            fontSize={"20"}
            fontColor={COLORS.white}
            backgroundColor={COLORS.black} />
        </div>
        <div style={styles.username}>
          <input
            type="text"
            placeholder="Username"
            style={styles.textInput}
            value={username}
            required
            onChange={(x) => {this.setState({username: x.target.value})}} />
        </div>
        <button style={styles.button} onClick={this.registerUser}> Register </button>
      </div>
    );
  }

  if (type === "1"){
    return <Artist account={account} contract={this.contract} ipfs={ipfs} />;
  }

  if (type === "2"){
    return <Audience account={account} contract={this.contract} ipfs={ipfs} />;
  }

  if (type === "3"){
    return <CommercialEntity account={account} contract={this.contract} ipfs={ipfs} />;
  }
}
}

const styles = {
main: {
  height: "100vh",
  display: "flex",
  flexDirection: "column",
  justifyContent: "space-around",
  alignItems: "center",
  background: COLORS.brown
},
button: {
  height: "7.5%",
  width: "15%",
  fontSize: "1.2rem",
  fontWeight: "500",
  cursor: "pointer",
  borderRadius: "50px",
  boxShadow: "2px 5px 2px #999",
  color: COLORS.white,
  background: COLORS.black,
},
img: {
  width: "32.5%",
  borderRadius: "100px",
  boxShadow: "2px 5px 2px #999",
},
switch: {
  height: "7.5%",
  width: "50%",
  fontSize: "1.2rem",
  fontWeight: "500",
},
form: {
  marginBottom: "5%",
  marginTop: "5%",
  padding: "5%",
  display: "flex",
  flexDirection: "column",
  alignItems: "center",
  borderRadius: "10px",
  border: "2px solid",
  overflow: "auto",
  gap: "20px",
  borderColor: COLORS.black,
  backgroundColor: COLORS.brown,
},
username: {
  height: "6%",
  width: "15%",
  fontSize: "1.2rem",
},
textInput: {
  height: "100%",
  borderRadius: "15px",
  padding: "3%",
  textAlign: "center",
},
}

export default Login;
